#include <cassert>
#include <iostream>

extern "C"
{

// These symbols should be generated by Ãœ compiler.
extern const char __U_sprache_compiler_version[];
extern const int __U_sprache_compiler_generation;

// Custom halt handler should be available.
extern void (*_U_halt_handler)();

} // extern "C"

static void TestHaltHandler(){}

int main()
{
	_U_halt_handler= TestHaltHandler;
	assert( __U_sprache_compiler_generation >= 0 );
	std::cout << "Compiler version: " << __U_sprache_compiler_version << ", generation: " << __U_sprache_compiler_generation << std::endl;
}
